static/style.css.content:
/* 
   SportOase Buchungssystem - Modern UI Design
   Modernes, cleanes Design inspiriert von zeitgemäßen Web-Apps
*/

:root {
    --primary: #38bdf8;
    --primary-dark: #0ea5e9;
    --primary-light: #e0f2fe;
    --accent: #10B981;
    --accent-warn: #F59E0B;
    --text-primary: #0F172A;
    --text-secondary: #64748B;
    --text-tertiary: #94A3B8;
    --bg-primary: #FFFFFF;
    --bg-secondary: #F8FAFC;
    --bg-tertiary: #F1F5F9;
    --border: #E2E8F0;
    --border-light: #F1F5F9;
    --shadow-sm: 0 1px 2px 0 rgba(0, 0, 0, 0.05);
    --shadow: 0 1px 3px 0 rgba(0, 0, 0, 0.1), 0 1px 2px -1px rgba(0, 0, 0, 0.1);
    --shadow-md: 0 4px 6px -1px rgba(0, 0, 0, 0.1), 0 2px 4px -2px rgba(0, 0, 0, 0.1);
    --shadow-lg: 0 10px 15px -3px rgba(0, 0, 0, 0.1), 0 4px 6px -4px rgba(0, 0, 0, 0.1);
    --radius-sm: 6px;
    --radius: 8px;
    --radius-lg: 12px;
    --radius-xl: 16px;
}

* {
    margin: 0;
    padding: 0;
    box-sizing: border-box;
}

body {
    font-family: -apple-system, BlinkMacSystemFont, 'Segoe UI', 'Inter', 'Roboto', 'Helvetica Neue', Arial, sans-serif;
    line-height: 1.5;
    color: var(--text-primary);
    background: var(--bg-secondary);
    -webkit-font-smoothing: antialiased;
    -moz-osx-font-smoothing: grayscale;
}

.container {
    max-width: 1400px;
    margin: 0 auto;
    padding: 0 2rem;
}

/* Top Navigation - Modern Minimal */
.top-nav {
    background: var(--bg-primary);
    border-bottom: 1px solid var(--border);
    padding: 0.75rem 0;
    backdrop-filter: blur(10px);
    position: sticky;
    top: 0;
    z-index: 100;
}

.top-nav nav {
    display: flex;
    gap: 0.5rem;
    justify-content: flex-end;
    align-items: center;
}

.top-nav nav a {
    color: var(--text-secondary);
    text-decoration: none;
    padding: 0.5rem 1rem;
    border-radius: var(--radius);
    transition: all 0.2s cubic-bezier(0.4, 0, 0.2, 1);
    font-weight: 500;
    font-size: 0.875rem;
}

.top-nav nav a:hover {
    background: var(--bg-tertiary);
    color: var(--text-primary);
}

/* Main Content */
main {
    min-height: 85vh;
    padding: 2rem 0;
}

h2 {
    font-size: 1.875rem;
    font-weight: 700;
    margin-bottom: 0.5rem;
    color: var(--text-primary);
    letter-spacing: -0.025em;
}

h3 {
    font-size: 1.25rem;
    font-weight: 600;
    margin: 1rem 0 0.75rem 0;
    color: var(--text-primary);
}

h4 {
    font-size: 1rem;
    font-weight: 600;
    margin: 0.75rem 0 0.5rem 0;
    color: var(--text-primary);
}

/* Flash Messages - Modern */
.messages {
    margin-bottom: 1.5rem;
}

.message {
    padding: 0.875rem 1rem;
    margin-bottom: 0.75rem;
    border-radius: var(--radius);
    font-size: 0.875rem;
    font-weight: 500;
    border-left: 3px solid;
    background: var(--bg-primary);
}

.message-success {
    border-color: var(--accent);
    color: var(--accent);
    background: #F0FDF4;
}

.message-error {
    border-color: #EF4444;
    color: #DC2626;
    background: #FEF2F2;
}

/* Login Page - Modern Centered Card */
.login-container {
    display: flex;
    align-items: center;
    justify-content: center;
    min-height: 100vh;
    background: linear-gradient(135deg, #7dd3fc 0%, #38bdf8 100%);
    padding: 1rem;
}

.login-box {
    background: var(--bg-primary);
    border-radius: var(--radius-xl);
    box-shadow: var(--shadow-lg);
    padding: 2.5rem;
    width: 100%;
    max-width: 420px;
}

.login-logo {
    text-align: center;
    margin-bottom: 2rem;
}

.login-logo img {
    width: 200px;
    height: 200px;
    border-radius: 50%;
    box-shadow: var(--shadow-md);
}

.login-box h2 {
    text-align: center;
    font-size: 1.5rem;
    margin-bottom: 2rem;
    color: var(--text-primary);
}

/* Form Inputs - Modern */
.form-group {
    margin-bottom: 1.25rem;
}

.form-group label {
    display: block;
    margin-bottom: 0.5rem;
    font-weight: 500;
    font-size: 0.875rem;
    color: var(--text-primary);
}

.form-group input,
.form-group select,
.form-group textarea {
    width: 100%;
    padding: 0.75rem;
    border: 1px solid var(--border);
    border-radius: var(--radius);
    font-size: 0.875rem;
    transition: all 0.2s;
    background: var(--bg-primary);
    color: var(--text-primary);
}

.form-group input:focus,
.form-group select:focus,
.form-group textarea:focus {
    outline: none;
    border-color: var(--primary);
    box-shadow: 0 0 0 3px var(--primary-light);
}

/* Buttons - Modern Minimal */
.btn, button[type="submit"] {
    display: inline-flex;
    align-items: center;
    justify-content: center;
    padding: 0.625rem 1.25rem;
    font-size: 0.875rem;
    font-weight: 500;
    border-radius: var(--radius);
    border: none;
    cursor: pointer;
    transition: all 0.2s cubic-bezier(0.4, 0, 0.2, 1);
    text-decoration: none;
    gap: 0.5rem;
}

.btn-primary {
    background: var(--primary);
    color: white;
}

.btn-primary:hover {
    background: var(--primary-dark);
    transform: translateY(-1px);
    box-shadow: var(--shadow-md);
}

.btn-secondary {
    background: var(--bg-tertiary);
    color: var(--text-primary);
}

.btn-secondary:hover {
    background: var(--border);
}

.btn-success {
    background: var(--accent);
    color: white;
}

.btn-success:hover {
    background: #059669;
}

.btn-block {
    background: #EF4444;
    color: white;
    font-size: 0.75rem;
    padding: 0.375rem 0.75rem;
}

.btn-block:hover {
    background: #DC2626;
}

.btn-small {
    font-size: 0.75rem;
    padding: 0.375rem 0.75rem;
}

/* Dashboard */
.dashboard {
    max-width: 100%;
}

.dashboard > h2 {
    margin-bottom: 2rem;
}

/* Date Selector - Modern */
.date-selector {
    background: var(--bg-primary);
    padding: 1rem;
    border-radius: var(--radius-lg);
    margin-bottom: 1.5rem;
    box-shadow: var(--shadow-sm);
    border: 1px solid var(--border);
}

.date-selector form {
    display: flex;
    align-items: center;
    gap: 0.75rem;
}

.date-selector label {
    font-weight: 500;
    font-size: 0.875rem;
    color: var(--text-secondary);
}

.date-selector input {
    padding: 0.5rem 0.75rem;
    border: 1px solid var(--border);
    border-radius: var(--radius);
    font-size: 0.875rem;
    background: var(--bg-primary);
    cursor: pointer;
    transition: all 0.2s;
}

.date-selector input:hover {
    border-color: var(--primary);
}

/* Selected Date Info */
.selected-date-info {
    margin-bottom: 2rem;
}

.selected-date-info h3 {
    background: linear-gradient(135deg, var(--primary) 0%, var(--primary-dark) 100%);
    color: white;
    padding: 1rem 1.5rem;
    margin: 0;
    border-radius: var(--radius-lg);
    text-align: center;
    font-size: 1.125rem;
    font-weight: 600;
    box-shadow: var(--shadow-md);
}

/* Week Overview - Modern Card */
.week-overview {
    background: var(--bg-primary);
    padding: 1.5rem;
    border-radius: var(--radius-xl);
    box-shadow: var(--shadow-sm);
    margin-bottom: 2rem;
    border: 1px solid var(--border);
}

.week-header-with-nav {
    display: flex;
    align-items: center;
    justify-content: space-between;
    margin-bottom: 1.5rem;
    padding-bottom: 1rem;
    border-bottom: 1px solid var(--border-light);
    gap: 1rem;
}

.week-header-with-nav h3 {
    color: var(--text-primary);
    font-size: 1rem;
    font-weight: 600;
    margin: 0;
    padding: 0;
    border: none;
    flex: 1;
    text-align: center;
}

.week-nav-arrow {
    display: flex;
    align-items: center;
    justify-content: center;
    width: 36px;
    height: 36px;
    background: var(--bg-tertiary);
    color: var(--text-secondary);
    border-radius: var(--radius);
    text-decoration: none;
    transition: all 0.2s;
    flex-shrink: 0;
    border: 1px solid var(--border);
}

.week-nav-arrow:hover {
    background: var(--primary);
    color: white;
    border-color: var(--primary);
}

.week-nav-arrow .arrow-icon {
    font-size: 1rem;
}

/* Week Table - Modern Grid */
.week-table {
    width: 100%;
    border-collapse: separate;
    border-spacing: 0;
    margin-top: 0;
}

.week-table th,
.week-table td {
    padding: 0.75rem;
    text-align: center;
    vertical-align: top;
    border: 1px solid var(--border-light);
}

.week-table th {
    background: var(--bg-tertiary);
    color: var(--text-primary);
    font-weight: 600;
    font-size: 0.75rem;
    text-transform: uppercase;
    letter-spacing: 0.05em;
    border-bottom: 2px solid var(--border);
}

.week-table th:first-child {
    border-top-left-radius: var(--radius);
}

.week-table th:last-child {
    border-top-right-radius: var(--radius);
}

.week-day-header {
    display: flex;
    flex-direction: column;
    gap: 0.25rem;
}

.day-name {
    font-size: 0.875rem;
    font-weight: 700;
    letter-spacing: 0.05em;
}

.day-date {
    font-size: 0.75rem;
    opacity: 0.7;
    font-weight: 500;
}

.week-table td {
    font-size: 0.8125rem;
    background: var(--bg-primary);
    transition: all 0.2s;
}

.week-table td:hover {
    background: var(--bg-secondary);
}

/* Period Styles */
.period-header {
    font-weight: 600;
    margin-bottom: 0.625rem;
    padding: 0.5rem;
    border-radius: var(--radius-sm);
    font-size: 0.75rem;
    text-align: center;
}

.period-fest .period-header {
    background: #FEF3C7;
    color: #92400E;
}

.period-frei .period-header {
    background: #D1FAE5;
    color: #065F46;
}

.period-blocked {
    background: #FEE2E2 !important;
}

.period-blocked:hover {
    background: #FECACA !important;
}

.blocked-header {
    background: #FEE2E2;
    color: #991B1B;
    border: 1px solid #FCA5A5;
    display: flex;
    flex-direction: column;
    align-items: center;
    gap: 0.25rem;
}

.blocked-icon {
    font-size: 1.5rem;
    font-weight: bold;
    color: #DC2626;
}

.period-past {
    opacity: 0.5;
    background: var(--bg-secondary) !important;
}

.slot-detail {
    font-size: 0.6875rem;
    font-weight: 400;
    margin-top: 0.25rem;
    opacity: 0.8;
}

/* Booking Info - Optimized */
.booking-info {
    margin-top: 0.5rem;
    padding: 0.5rem;
    background: var(--bg-secondary);
    border-radius: var(--radius-sm);
    font-size: 0.6875rem;
    text-align: left;
}

.slot-stat {
    font-weight: 600;
    color: var(--text-secondary);
    margin-bottom: 0.375rem;
    text-align: center;
}

.booking-entry {
    margin-top: 0.375rem;
    padding-top: 0.375rem;
    border-top: 1px solid var(--border-light);
}

.booking-entry:first-child {
    margin-top: 0;
    padding-top: 0;
    border-top: none;
}

.booking-title {
    font-weight: 600;
    color: var(--text-primary);
    margin-bottom: 0.25rem;
}

.student-name {
    font-size: 0.6875rem;
    color: var(--text-secondary);
    margin-left: 0.5rem;
}

/* Slot Actions - All Buttons Equal Size */
.slot-actions {
    margin-top: 0.625rem;
    display: flex;
    flex-direction: column;
    gap: 0.375rem;
}

.slot-actions form {
    margin: 0;
}

.slot-btn {
    display: block;
    width: 100%;
    padding: 0.5rem 0.75rem;
    font-size: 0.6875rem;
    font-weight: 600;
    text-align: center;
    border-radius: var(--radius-sm);
    border: none;
    cursor: pointer;
    transition: all 0.2s;
    text-decoration: none;
}

.slot-btn-primary {
    background: var(--primary);
    color: white;
}

.slot-btn-primary:hover {
    background: var(--primary-dark);
}

.slot-btn-success {
    background: var(--accent);
    color: white;
}

.slot-btn-success:hover {
    background: #059669;
}

.slot-btn-block {
    background: #DC2626;
    color: white;
}

.slot-btn-block:hover {
    background: #B91C1C;
}

.slot-btn-disabled {
    background: var(--bg-tertiary);
    color: var(--text-tertiary);
    cursor: not-allowed;
}

/* Day Schedule - Modern Table */
.day-schedule {
    background: var(--bg-primary);
    padding: 1.5rem;
    border-radius: var(--radius-xl);
    box-shadow: var(--shadow-sm);
    border: 1px solid var(--border);
}

.day-schedule h3 {
    margin-bottom: 1.25rem;
    font-size: 1.125rem;
}

.schedule-table {
    width: 100%;
    border-collapse: separate;
    border-spacing: 0;
}

.schedule-table th,
.schedule-table td {
    padding: 0.875rem;
    text-align: left;
    border-bottom: 1px solid var(--border-light);
}

.schedule-table th {
    background: var(--bg-tertiary);
    color: var(--text-secondary);
    font-weight: 600;
    font-size: 0.75rem;
    text-transform: uppercase;
    letter-spacing: 0.05em;
}

.schedule-table tbody tr:hover {
    background: var(--bg-secondary);
}

.schedule-table tbody tr:last-child td {
    border-bottom: none;
}

.blocked-row {
    background: #FEF2F2 !important;
}

.past-row {
    opacity: 0.5;
}

.unbookable-row {
    opacity: 0.6;
    background: var(--bg-tertiary) !important;
    pointer-events: none;
}

.unbookable-row td {
    color: var(--text-tertiary);
}

.weekend-row {
    opacity: 0.5;
    background: #FEF3C7 !important;
}

.time-restricted-row {
    opacity: 0.65;
    background: #FEF3C7 !important;
}

.capacity {
    display: inline-block;
    padding: 0.25rem 0.625rem;
    background: var(--bg-tertiary);
    border-radius: var(--radius-sm);
    font-size: 0.8125rem;
    font-weight: 500;
}

.capacity.full {
    background: #FEE2E2;
    color: #991B1B;
}

.blocked-text {
    color: #DC2626;
    font-weight: 600;
}

.time-message {
    color: var(--text-tertiary);
    font-size: 0.75rem;
    font-style: italic;
    margin-top: 0.25rem;
}

/* Booking Form - Modern */
.booking-form {
    background: var(--bg-primary);
    padding: 2rem;
    border-radius: var(--radius-xl);
    box-shadow: var(--shadow-sm);
    border: 1px solid var(--border);
    max-width: 600px;
    margin: 0 auto;
}

.booking-info-box {
    background: var(--bg-secondary);
    padding: 1rem;
    border-radius: var(--radius);
    margin-bottom: 1.5rem;
    border-left: 3px solid var(--primary);
}

.student-list {
    list-style: none;
    margin-bottom: 1.5rem;
}

.student-item {
    display: flex;
    gap: 0.75rem;
    margin-bottom: 0.75rem;
}

.student-item input {
    flex: 1;
}

.btn-remove {
    padding: 0.5rem 0.75rem;
    background: #FEE2E2;
    color: #DC2626;
    border: none;
    border-radius: var(--radius);
    cursor: pointer;
    font-size: 0.875rem;
    transition: all 0.2s;
}

.btn-remove:hover {
    background: #FEF2F2;
}

.btn-add {
    padding: 0.5rem 1rem;
    background: var(--bg-tertiary);
    color: var(--text-primary);
    border: 1px dashed var(--border);
    border-radius: var(--radius);
    cursor: pointer;
    font-size: 0.875rem;
    font-weight: 500;
    transition: all 0.2s;
}

.btn-add:hover {
    border-color: var(--primary);
    background: var(--primary-light);
    color: var(--primary);
}

/* Admin Panel - Modern Layout */
.admin-panel {
    display: grid;
    gap: 1.5rem;
}

.admin-section {
    background: var(--bg-primary);
    padding: 1.5rem;
    border-radius: var(--radius-xl);
    box-shadow: var(--shadow-sm);
    border: 1px solid var(--border);
}

.admin-section h3 {
    margin-bottom: 1.25rem;
    padding-bottom: 0.75rem;
    border-bottom: 1px solid var(--border-light);
}

.user-list,
.booking-list {
    list-style: none;
}

.user-item,
.booking-item {
    padding: 0.875rem;
    border-bottom: 1px solid var(--border-light);
    display: flex;
    justify-content: space-between;
    align-items: center;
}

.user-item:last-child,
.booking-item:last-child {
    border-bottom: none;
}

.user-item:hover,
.booking-item:hover {
    background: var(--bg-secondary);
}

.badge {
    display: inline-block;
    padding: 0.25rem 0.625rem;
    border-radius: var(--radius-sm);
    font-size: 0.75rem;
    font-weight: 600;
}

.badge-admin {
    background: #DBEAFE;
    color: #1E40AF;
}

.badge-teacher {
    background: #D1FAE5;
    color: #065F46;
}

/* Filter Form */
.filter-form {
    display: flex;
    gap: 0.75rem;
    margin-bottom: 1.5rem;
    align-items: flex-end;
}

/* Responsive - Mobile Optimierung */
@media (max-width: 768px) {
    /* Container & Spacing */
    .container {
        padding: 0 0.75rem;
    }

    main {
        padding: 1rem 0;
    }

    /* Typography */
    h2 {
        font-size: 1.5rem;
    }

    h3 {
        font-size: 1.125rem;
    }

    /* Login Page */
    .login-container {
        padding: 0.5rem;
    }

    .login-box {
        padding: 1.5rem 1rem;
        max-width: 100%;
    }

    .login-logo img {
        width: 120px;
        height: 120px;
    }

    /* Navigation */
    .top-nav {
        padding: 0.5rem 0;
    }

    .top-nav nav {
        flex-wrap: wrap;
        gap: 0.25rem;
    }

    .top-nav nav a {
        font-size: 0.8125rem;
        padding: 0.4rem 0.75rem;
    }

    /* Buttons - Größer für Touch */
    .btn, button[type="submit"] {
        padding: 0.75rem 1rem;
        font-size: 0.9375rem;
        min-height: 44px;
    }

    .btn-small {
        padding: 0.5rem 0.75rem;
        font-size: 0.8125rem;
    }

    /* Form Inputs - Größer für Touch */
    .form-group input,
    .form-group select,
    .form-group textarea {
        padding: 0.875rem;
        font-size: 1rem;
        min-height: 44px;
    }

    /* Date Selector */
    .date-selector {
        padding: 0.75rem;
    }

    .date-selector form {
        flex-direction: column;
        gap: 0.5rem;
    }

    .date-selector input {
        width: 100%;
    }

    /* Week Overview */
    .week-overview {
        padding: 0.75rem;
        overflow-x: auto;
        -webkit-overflow-scrolling: touch;
    }

    .week-header-with-nav {
        flex-wrap: nowrap;
        gap: 0.5rem;
        margin-bottom: 1rem;
    }

    .week-header-with-nav h3 {
        font-size: 0.75rem;
        white-space: nowrap;
        overflow: hidden;
        text-overflow: ellipsis;
    }

    .week-nav-arrow {
        width: 32px;
        height: 32px;
        min-width: 32px;
    }

    /* Week Table - Horizontal Scroll */
    .week-table {
        min-width: 600px;
        font-size: 0.6875rem;
    }

    .week-table th,
    .week-table td {
        padding: 0.375rem 0.25rem;
        font-size: 0.6875rem;
    }

    .day-name {
        font-size: 0.75rem;
    }

    .day-date {
        font-size: 0.6875rem;
    }

    .period-header {
        font-size: 0.6875rem;
        padding: 0.375rem;
        margin-bottom: 0.5rem;
    }

    .booking-info {
        font-size: 0.625rem;
        padding: 0.375rem;
        margin-top: 0.375rem;
    }

    .slot-stat {
        font-size: 0.625rem;
        margin-bottom: 0.25rem;
    }

    .booking-entry {
        margin-top: 0.25rem;
        padding-top: 0.25rem;
    }

    .booking-title {
        font-size: 0.625rem;
    }

    .student-name {
        font-size: 0.5625rem;
    }

    .slot-actions {
        gap: 0.25rem;
        margin-top: 0.5rem;
    }

    .slot-btn {
        padding: 0.375rem 0.5rem;
        font-size: 0.625rem;
        min-height: 28px;
    }

    /* Day Schedule */
    .day-schedule {
        padding: 1rem;
        overflow-x: auto;
        -webkit-overflow-scrolling: touch;
    }

    .schedule-table {
        min-width: 600px;
        font-size: 0.8125rem;
    }

    .schedule-table th,
    .schedule-table td {
        padding: 0.625rem 0.5rem;
        font-size: 0.8125rem;
    }

    /* Booking Form */
    .booking-form {
        padding: 1rem;
    }

    .booking-info-box {
        padding: 0.75rem;
        margin-bottom: 1rem;
    }

    .booking-info-box h3 {
